import React, { Component } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { Home, Dashboard, RegistrationModal } from "./components";

class App extends Component {
  state = {
    showModal: false,
    showModalLogin: false,
    showModalSignUp: false,
  };

  showModal = (tab) => {
    this.setState({ showModal: true });
  };

  getModalTab = () => {
    console.log("Get Tab Called");
    if (this.state.showModalLogin) {
      return "login";
    } else if (this.state.showModalSignUp) {
      return "sign-up";
    }
  };

  // TODO Hide Modal When Clicked Out Of
  hideModal = () => {
    this.setState({ showModal: false });
  };

  // TODO Implement Switching Between Login & Sign Up Tabs

  render() {
    return (
      <Router>
        <Routes>
          <Route path="/" element={<Home onShowModal={this.showModal} />} />
          <Route
            path="/dashboard"
            element={<Dashboard onShowModal={this.showModal} />}
          />
        </Routes>
        {this.state.showModal && (
          <RegistrationModal
            onShowModal={this.showModal}
            onHideModal={this.hideModal}
            onGetModalVisibility={this.getModalVisibility}
            onGetModalTab={this.getModalTab}
          />
        )}
      </Router>
    );
  }
}

export default App;
